// from https://usehooks.com/useAuth/

import React, { useState, useEffect, useContext, createContext } from "react";
import axios from "axios";

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export function AuthProvider({ children }) {
  const [user, setUser] = useState();

  useEffect(() => {
    console.log("useEffect");

    // declare the data fetching function
    const meQuery = async () => {
      const response = await axios.get(
        process.env.REACT_APP_API_URL + "/users/me",
        {
          withCredentials: true,
        }
      );
    };

    try {
      console.log("making a Me query!");
      axios.get.then((response) => {
        console.log("maawefawefuery!");
        if (response.data) {
          console.log("Received a user:");
          console.log(response.data);
          setUser(response.data);
        } else {
          setUser(null);
          console.log("Not logged in");
        }
      });
    } catch (error) {
      console.log(error);
    }
    // console.log("Use effect Started");
    // const user = JSON.parse(sessionStorage.getItem("user"));
    // if (user) {
    //   console.log("user found in session storage:");
    //   setUser(user);
    //   console.log(user);
    // } else {
    //   console.log("user not found in session storage.");
    //   setUser(null);
    // }

    // console.log(user);
    console.log("useEffect Ends");
  }, []);

  const login = async (username, password) => {
    try {
      const response = await axios.post(
        process.env.REACT_APP_API_URL + "/users/login",
        {
          username: username,
          password: password,
        },
        { withCredentials: true }
      );
      setUser(response.data);
      return response.data;
    } catch (error) {
      return null;
    }
  };
  const register = (email, password) => {
    console.log("register() is NOT IMPLEMENTED");
    return null;
    //return user
  };
  const logout = async () => {
    try {
      const response = await axios.get(
        process.env.REACT_APP_API_URL + "/users/logout",
        { withCredentials: true }
      );
      setUser(null);
    } catch (error) {
      return null;
    }
  };

  const auth = {
    user,
    login,
    logout,
    register,
  };

  return <AuthContext.Provider value={auth}>{children}</AuthContext.Provider>;
}
