// from https://usehooks.com/useAuth/

import React, { useState, useEffect, useContext, createContext } from "react";
import axios from "axios";

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export function AuthProvider({ children }) {
  const [user, setUser] = useState();

  useEffect(() => {
    axios.get(process.env.REACT_APP_API_URL + "/users/me").then((response) => {
      if (response.data.loggedIn === true) {
        setUser(response.data.user);
      } else {
        setUser(null);
      }
      console.log(response.data.user);
    });

    console.log("Use effect Started");
    const user = JSON.parse(sessionStorage.getItem("user"));
    if (user) {
      console.log("user found in session storage:");
      setUser(user);
      console.log(user);
    } else {
      console.log("user not found in session storage.");
      setUser(null);
    }

    console.log(user);
    console.log("useEffect Ends");
  }, []);

  const login = async (username, password) => {
    try {
      const response = await axios.post(
        process.env.REACT_APP_API_URL + "/users/login",
        {
          username: username,
          password: password,
        },
        { withCredentials: true }
      );
      setUser(response.data);
      sessionStorage.setItem("user", JSON.stringify(response.data));
      return response.data;
    } catch (error) {
      return null;
    }
  };
  const register = (email, password) => {
    console.log("register() is NOT IMPLEMENTED");
    return null;
    //return user
  };
  const logout = () => {
    sessionStorage.clear();
    setUser(null);
  };

  const auth = {
    user,
    login,
    logout,
    register,
  };

  return <AuthContext.Provider value={auth}>{children}</AuthContext.Provider>;
}
