// from https://usehooks.com/useAuth/

import React, { useState, useEffect, useContext, createContext } from "react";
import axios from "./axios";

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export function AuthProvider({ children }) {
  const [user, setUser] = useState();

  // This effect runs on refresh / component rendering
  // Saves user into AuthProvider context (read from localStorage)
  useEffect(() => {
    async function meQuery() {
      try {
        const response = await axios.get("/users/me");
        localStorage.clear();
        setUser(null);
      } catch (error) {
        return null;
      }
    }

    /*
    console.log("Use effect Started");
    const user = JSON.parse(localStorage.getItem("user"));
    if (user) {
      console.log("user found in session storage:");
      setUser(user);
      console.log(user);
    } else {
      console.log("user not found in session storage.");
      setUser(null);
    }

    console.log(user);
    console.log("useEffect Ends");*/
  }, []);

  // Logs in user
  const login = async (username, password) => {
    try {
      const response = await axios.post("/users/login", {
        username: username,
        password: password,
      });
      setUser(response.data);
      localStorage.setItem("user", JSON.stringify(response.data));
      return response.data;
    } catch (error) {
      return null;
    }
  };

  // Register new user
  const register = (email, password) => {
    console.log("register() is NOT IMPLEMENTED");
    return null;
    //return user
  };

  //Logs out user
  const logout = async () => {
    try {
      const response = await axios.get("/users/logout");
      localStorage.clear();
      setUser(null);
    } catch (error) {
      return null;
    }
  };

  const auth = {
    user,
    login,
    logout,
    register,
  };

  return <AuthContext.Provider value={auth}>{children}</AuthContext.Provider>;
}
